document.addEventListener("DOMContentLoaded", () => {
    let counter = document.getElementById("counter");
    let likeList = document.querySelector(".likes");
    let commentList = document.getElementById("list");
    let interval = setInterval(increaseCounter, 1000);
    let paused = false;
    
    document.getElementById("plus").addEventListener("click", () => {
        counter.textContent = parseInt(counter.textContent) + 1;
    });

    document.getElementById("minus").addEventListener("click", () => {
        counter.textContent = parseInt(counter.textContent) - 1;
    });

    document.getElementById("heart").addEventListener("click", () => {
        let num = counter.textContent;
        let existingLike = document.getElementById(`like-${num}`);
        if (existingLike) {
            let likeCount = existingLike.querySelector(".like-count");
            likeCount.textContent = parseInt(likeCount.textContent) + 1;
        } else {
            let li = document.createElement("li");
            li.id = `like-${num}`;
            li.innerHTML = `${num} has been liked <span class="like-count">1</span> time(s).`;
            likeList.appendChild(li);
        }
    });

    document.getElementById("pause").addEventListener("click", (event) => {
        if (!paused) {
            clearInterval(interval);
            event.target.textContent = "resume";
            document.querySelectorAll("button:not(#pause)").forEach(btn => btn.disabled = true);
        } else {
            interval = setInterval(increaseCounter, 1000);
            event.target.textContent = "pause";
            document.querySelectorAll("button").forEach(btn => btn.disabled = false);
        }
        paused = !paused;
    });

    document.getElementById("comment-form").addEventListener("submit", (event) => {
        event.preventDefault();
        let comment = document.getElementById("comment-input").value;
        if (comment.trim() !== "") {
            let p = document.createElement("p");
            p.textContent = comment;
            commentList.appendChild(p);
            event.target.reset();
        }
    });

    function increaseCounter() {
        counter.textContent = parseInt(counter.textContent) + 1;
    }
});
